import { createSlice } from "@reduxjs/toolkit";

const bracketSlice = createSlice({
    name: 'bracket',
    initialState: {
        id: '',
        bracket: '',
    },
    reducers: {
        setBracket(state, action) {
            state.id = action.payload.id
            state.bracket = action.payload.bracket

        },

        clearBracket(state) {
            state.id = null
            state.token = null
            state.is_superuser = null
            state.mo = null
            state.po = null
            state.mo_name = null
            state.po_name = null
            state.mo_rights = null
            state.po_rights = null
            state.uname = null
            state.detail = null
            localStorage.clear()
        },
    }
})

export const { setBracket, clearBracket } = bracketSlice.actions;

export default bracketSlice.reducer;
