import React, { useContext } from "react";
import classes from "./MyNavbar.module.css";
import { AuthContext } from "../../../context";
import { Container, Nav, Navbar, Dropdown } from "react-bootstrap";
import ThemeSwitcher from "../ThemeSwitcher/ThemeSwitcher";
import SettingIcon from "../../../assets/svg/SettingIcon";
import HomeIcon from "../../../assets/svg/HomeIcon";
import { Link } from 'react-router-dom';


const MyNavbar = () => {
  const { user, logoutUser } = useContext(AuthContext);
  return (
    <Navbar className={`${classes.my_navbar} mb-3`} abg="dark" expand="lg">
      <Container fluid>
        <Navbar.Brand as={Link} to="/">
          <HomeIcon />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="800"
          height="800"
          ariaHidden="true"
          viewBox="0 0 14 14"
        >
          <path d="M10.135 10.265h.351v.373h-.351zm-.659-1.356l-.175.305.834.481v.403h.351v-.606zm-3.224 1.356h.351v.373h-.351zm-.659-1.356l-.175.305.834.481v.403h.351v-.606zm4.542-5.37h.351v.373h-.351zm-.659-1.355l-.175.304.834.482v.402h.351v-.605zM6.252 3.539h.351v.373h-.351zm-.659-1.355l-.175.304.834.482v.402h.351v-.605zm6.483 4.718h.352v.373h-.352zm-.658-1.356l-.176.305.834.481v.403h.352v-.606zm-.36-.89V2.415L8.942 1.193 7 2.313l-1.941-1.12L2.94 2.415v2.241L1 5.777v2.445l1.941 1.12v2.243l2.118 1.222L7 11.687l1.941 1.12 2.118-1.222V9.343L13 8.222V5.777l-1.941-1.12zM7.177 2.618l1.765-1.02 1.766 1.02v2.038l-1.766 1.02-1.765-1.02V2.618zm-3.883 0l1.766-1.02 1.765 1.02v2.038L5.06 5.676l-1.766-1.02V2.618zM1.352 8.019V5.98l1.765-1.019 1.766 1.02v2.038l-1.766 1.02-1.765-1.02zm5.472 3.363l-1.765 1.02-1.766-1.02V9.343l1.766-1.019 1.765 1.02v2.038zM5.234 8.02V5.98L7 4.961l1.766 1.02v2.038L7 9.039l-1.766-1.02zm3.707 4.382l-1.765-1.019V9.343L8.94 8.324l1.766 1.02v2.038l-1.766 1.02zm3.707-4.382l-1.765 1.02-1.766-1.02V5.98l1.766-1.019 1.765 1.02v2.038zM8.193 6.902h.352v.373h-.352zm-.658-1.356l-.176.305.834.481v.403h.352v-.606zM4.31 6.902h.351v.373h-.351zm-.659-1.356l-.176.305.835.481v.403h.351v-.606z"></path>
        </svg>
        </Navbar.Toggle>
        <Navbar.Collapse id="navbarScroll">
          <Nav className="me-auto  my-lg-0" navbarScroll>
            <Link className={classes.nav_link} to="/tournaments">
              <h5>Tournaments</h5>
            </Link>
            <Link className={classes.nav_link} to="/create_tournament">
              <h5>Create Tournament</h5>
            </Link>
          </Nav>
          <ThemeSwitcher></ThemeSwitcher>
          {user ? (
            <>
              <Nav className="ml-auto" navbar>
                <Dropdown>
                  <Dropdown.Toggle
                    drop="down"
                    className={`${classes.my_toggle} ${"shadow-none"}`}
                  >
                    <SettingIcon />
                  </Dropdown.Toggle>
                  <Dropdown.Menu className={classes.my_drop}>
                    <Dropdown.Item
                      className={classes.nav_item}
                      as={Link}
                      to={`/profile/${user.username}`}
                    >
                      Profile
                    </Dropdown.Item>
                    <Dropdown.Item
                      className={classes.nav_item}
                      as={Link}
                      to="/"
                      onClick={logoutUser}
                    >
                      Log out
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Nav>
            </>
          ) : (
            <>
              <Nav className="my-lg-0" navbarScroll>
                <Link to="/login" className={classes.nav_link}>
                  <h5>Log in</h5>
                </Link>
                <Link to="/register" className={classes.nav_link}>
                  <h5>Sign up</h5>
                </Link>
              </Nav>

            </>
          )}
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default MyNavbar;
