import { createSlice } from "@reduxjs/toolkit";

const userSlice = createSlice({
    name: 'user',
    initialState: {
        subscriptions: []
    },
    reducers: {
        setUser(state, action) {

        },

        followTournament(state, action) {
            state.subscriptions = [...state.subscriptions, action.payload.moderator]
        },

        unFollowTournament(state, action) {
            state.moderators = state.moderators.filter((username) => username != action.payload.moderator)
        },

        clearUser(state) {

        },
    }
})

export const { setUser, followTournament, unFollowTournament, clearUser } = userSlice.actions;

export default userSlice.reducer;
