import MyModal from "../../../UI/MyModal/MyModal";
import Modal from "react-bootstrap/Modal";
import classes from "./InfoModal.module.css";
import moment from "moment";

const InfoModal = ({modalShow, setMatchCardModalShow, match}) => {
    <MyModal show={modalShow} onHide={() => setMatchCardModalShow(false)}>
    <Modal.Header closeButton className={classes.myModalHeader}>
      <div className={classes.matchTitle}>
        {moment(match.startTime).format("dddd HH:mm") || ""}
      </div>
    </Modal.Header>
    <Modal.Body className={classes.myModalBody}>
      <div className={classes.divVS}>
        <div className="row align-items-center">
          <div className="col">
            {match.teams[0]?.participant || "NO TEAM "}
          </div>
          <div className="col"></div>
          <div className="col">
            {match.teams[1]?.participant || "NO TEAM "}
          </div>
        </div>
        <div className="row align-items-center">
          <div className="col">{match.teams[0].score}</div>
          <div className="col">
            <h4>VS</h4>
          </div>
          <div className="col">{match.teams[1].score}</div>
        </div>
      </div>
    </Modal.Body>
  </MyModal>
};

export default InfoModal;
