import React, {useEffect} from 'react';
import Form from 'react-bootstrap/Form';
import classes from "./MyFormGroupInput.module.css";
import { useController } from "react-hook-form";

const MyFormGroupInput = ({ label, defaultValue, name, trigger, control, as, type, onChange, register = () => {}, validationSchema, errors, className}) => {

    const inputChangeHandler = (event) => {

        onChange(event.target)
    }

    const { field, fieldState } = useController({ control, name: name });

    useEffect(() => {
        // console.log("field", field);
        // console.log("defaultValue", defaultValue);
        // console.log("name", name);

        if (field.value !== defaultValue && !fieldState.isTouched) {
          trigger(name, { shouldFocus: true });
        }
      }, [defaultValue, field.value, fieldState.isTouched, trigger]);

    return (
        <Form.Group className="mb-3">
            <Form.Label className={`${classes.myFormLabel}`}>{label}</Form.Label>
            <Form.Control
                {...register(name, validationSchema)}
                type={type}
                as={as}
                onChange={inputChangeHandler}
                {...field}
                className={`${classes.myInput} ${className} shadow-none`}
            />
        {errors && errors[name] && <p className={classes.warningP} role="alert">{errors[name]?.message}</p>}
        </Form.Group>
    );
};

export default MyFormGroupInput;
