import axios from "axios";
import useAxios from "../../API/useAxios";

const API_SERVER = "http://127.0.0.1:8000/api/v1"


const bracketApi = {
    getBracket: async (id) => {axios.get(`${API_SERVER}/bracket/${id}`)},
    getBrackets: async (id) =>  axios.get(`${API_SERVER}/tournament_brackets/${id}/`),
    updateBracket: async (data) =>  axios.put(`${API_SERVER}/update_bracket/`, data),

    createModerator: async (data) =>  axios.post(`${API_SERVER}/create_moderator/`, data),
    deleteModerator: async (data) =>  axios.delete(`${API_SERVER}/delete_moderator/`, data),


}

export default bracketApi
