    // );
    // setTypes(response.data[response.data.length - 1].type);
    // setId(response.data[response.data.length - 1].id);
  });

  useEffect(() => {
    fetchTournament(params.link);
  }, []);

  useEffect(() => {
    if (tournament.id) {
      fetchBrackets();
    }
  }, [tournament.id])

  useEffect(() => { }, [bracket]);

  return (
    <section>
      {isLoading ? (
        <div className="loader">
          <Loader />
        </div>
      ) : (
        <div className="container">
          <div className="row">
            <div className="col-lg-12 col-md-12">
              <div className="row">
                <div className="col-sm-8">
                  {tournament.poster == null ? (
                    <div className={`${classes.tournament_default}`}>
                      <DefaultTournamnetPoster />
                    </div>
                  ) : (
                    <img
                      src={tournament.poster}
                      className={`${classes.tournament_img}`}
                      alt="card text"
                    />
                  )}
                </div>
                <div className="col-sm-4">
                  <div className="d-flex flex-column pt-1">
                    <h3 className="tournament_text">{tournament.title}</h3>
                    <p>Start of the tournament</p>
                    <p className="tournament_text">
                      {moment(tournament.start_time).format(
                        " Do MMMM  YYYY, hh:mm"
                      ) || ""}
                    </p>
                    <p>Game</p>
                    <p className="tournament_text">{tournament.game}</p>
                    <p>Prize fund</p>
                    <p className="tournament_text">
                      {tournament.prize} <span>&#8381;</span>
                    </p>
                    <div className={`${classes.tournament_block}`}>
                      <p>Organizer</p>
                      <p className="tournament_text ">{tournament.owner}</p>
                    </div>
                  </div>
                </div>
              </div>
              <div className="row my-3">
                <div className="col">
                  <Accordion flush defaultActiveKey={["1", "2"]} alwaysOpen>
                    <Accordion.Item eventKey="0">
                      <Accordion.Header className="my_accordion_body">
                        <h4>Description</h4>
                      </Accordion.Header>
                      <Accordion.Body className="my_accordion_body">
                        <p>{tournament.content}</p>
                      </Accordion.Body>
                    </Accordion.Item>
                    {groupStage.length > 0 ? (
                      <>
                        <Accordion.Item eventKey="1">
                          <Accordion.Header className="my_accordion_body">
                            <h4>Group stage</h4>
                          </Accordion.Header>
                          <Accordion.Body className="my_accordion_body">
                            {isBraLoadind ? (
                              <div className="loader">
                                <Loader />
                              </div>
                            ) : (
                              <>
                                {groupStage.map((bracket, idx) => {
                                  if (bracket[1] === "RR") {
                                    return (
                                      <div className="mb-5">
                                        <h5>Group {idx}</h5>
                                        <RoundRobin
                                          id={bracket[0]}
                                          bracket={bracket[2]}
                                          owner={tournament.owner}
                                        />
                                      </div>
                                    );
                                  } else if (bracket[1] === "SE") {
                                    return (
                                      <div className="mb-5">
                                        <h5>Group {idx}</h5>
                                        <SingleEl
                                          id={bracket[0]}
                                          bracket={bracket[2]}
                                          owner={tournament.owner}
                                        />
                                      </div>
                                    );
                                  } else if (bracket[1] === "DE") {
                                    return (
                                      <div className="mb-5">
                                        <h5>Group {idx}</h5>
                                        <DoubleEl
                                          id={bracket[0]}
                                          bracket={bracket[2]}
                                          owner={tournament.owner}
                                        />
                                      </div>
                                    );
                                  } else if (bracket[1] === "SW") {
                                    return (
                                      <div className="mb-5">
                                        <h5>Group {idx}</h5>
                                        <Swiss
                                          id={bracket[0]}
                                          bracket={bracket[2]}
                                          owner={tournament.owner}
                                        />
                                      </div>
                                    );
                                  }
                                })}
                              </>
                            )}
                          </Accordion.Body>
                        </Accordion.Item>
                        <Accordion.Item eventKey="2">
                          <Accordion.Header className="my_accordion_body">
                            <h4>Final stage</h4>
                          </Accordion.Header>
                          <Accordion.Body className="my_accordion_body">
                            {isBraLoadind ? (
                              <div className="loader">
                                <Loader />
                              </div>
                            ) : (
                              <>
                                {(() => {
                                  if (types == "SE") {
                                    return (
                                      <SingleEl
                                        id={id}
                                        bracket={bracket}
                                        owner={tournament.owner}
                                      />
                                    );
                                  } else if (types == "RR") {
                                    return (
                                      <RoundRobin
                                        id={id}
                                        bracket={bracket}
                                        owner={tournament.owner}
                                      />
                                    );
                                  } else if (types == "DE") {
                                    return (
                                      <DoubleEl
                                        id={id}
                                        bracket={bracket}
                                        owner={tournament.owner}
                                      />
                                    );
                                  } else if (types == "SW") {
                                    return (
                                      <Swiss
                                        id={id}
                                        bracket={bracket}
                                        owner={tournament.owner}
                                      />
                                    );
                                  }
                                })()}
                              </>
                            )}
                          </Accordion.Body>
                        </Accordion.Item>
                      </>
                    ) : (
                      <Accordion.Item eventKey="1">
                        <Accordion.Header className="my_accordion_body">
                          <h4>Bracket</h4>
                        </Accordion.Header>
                        <Accordion.Body className="my_accordion_body">
                          {isBraLoadind ? (
                            <div className="loader">
                              <Loader />
                            </div>
                          ) : (
                            <>
                              {" "}
                              {(() => {
                                if (types == "SE") {
                                  return (
                                    <SingleEl
                                      id={id}
                                      bracket={bracket}
                                      owner={tournament.owner}
                                    />
                                  );
                                } else if (types == "RR") {
                                  return (
                                    <RoundRobin
                                      id={id}
                                      bracket={bracket}
                                      owner={tournament.owner}
                                    />
                                  );
                                } else if (types == "DE") {
                                  return (
                                    <DoubleEl
                                      id={id}
                                      bracket={bracket}
                                      owner={tournament.owner}
                                    />
                                  );
                                } else if (types == "SW") {
                                  return (
                                    <Swiss
                                      id={id}
                                      bracket={bracket}
                                      owner={tournament.owner}
                                    />
                                  );
                                }
                              })()}
                            </>
                          )}
                        </Accordion.Body>
                      </Accordion.Item>
                    )}
                  </Accordion>
                </div>
              </div>
              {/* {user !== null && tournament.owner == user.username ? ( */}
              <>
                <MyButton
                  additionalCl={"btn-md btn my-3 me-3"}
                  type="submit"
                  onClick={onEdit}
                >
                  Edit Tournament
                </MyButton>
                <MyButton
                  additionalCl={"btn-md btn my-3 me-3"}
                  type="submit"
                  onClick={onDelete}
                >
                  Delete
                </MyButton>
              </>
              {/* // ) : (
              //   <></>
              // )} */}
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default Tournament;
