import { useState, useContext, Fragment } from "react";
import classes from "./BracketController.module.css";
import SingleElimination from "../Brackets/SE/SingleElimination";
import DoubleElimination from "../Brackets/DE/DoubleElimination";
import RoundRobin from "../Brackets/RR/RoundRobin";
import Swiss from "../Brackets/SW/Swiss";
import { useSelector, useDispatch } from "react-redux";


const BracketController = () => {
    const bracket = useSelector(state => state.bracket)
  return (
    <Fragment>
    {bracket.brackets.map((br) => {
      if (br.type === 1) {
          return <SingleElimination
            bracket={br.rounds}
            bracketId={br.id}
          />
      } else if (br.type === 2) {
          return <DoubleElimination
            bracket={br.rounds}
            bracketId={br.id}
          />
      } else if (br.type == 3) {
          return <RoundRobin
            bracket={br.rounds}
            bracketId={br.id}
          />
      } else if (br.type == 4) {
          return <Swiss
            bracket={br.rounds}
            bracketId={br.id}
          />
      }
    })
    }
  </Fragment>
  );
};

export default BracketController;
