import React, { useContext } from "react";
import classes from "./MyNavbar.module.css";
import { AuthContext } from "../../../context";
import { Container, Nav, Navbar, Dropdown } from "react-bootstrap";
import ThemeSwitcher from "../ThemeSwitcher/ThemeSwitcher";
import SettingIcon from "../../../assets/svg/SettingIcon";
import HomeIcon from "../../../assets/svg/HomeIcon";
import { Link } from 'react-router-dom';


const MyNavbar = () => {
  const { user, logoutUser } = useContext(AuthContext);
  return (
    <Navbar className={`${classes.my_navbar} mb-3`} abg="dark" expand="lg">
      <Container fluid>
        <Navbar.Brand as={Link} to="/">
          <HomeIcon />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll">
        <svg
      xmlns="http://www.w3.org/2000/svg"
      width="30"
      height="800"
      className="icon"
      viewBox="0 0 1024 1024"
    >
      <path d="M786.359 809.326a88.357 88.357 0 01176.567 0 88.283 88.283 0 01-176.567 0zM511.56 898.56c-48.64 0-88.21-39.497-88.21-88.064a88.21 88.21 0 01176.42 0c0 48.567-39.57 88.064-88.21 88.064zm.951-839.022c48.567 0 88.137 39.497 88.137 87.918a88.137 88.137 0 01-176.201 0c0-48.494 39.497-87.918 88.064-87.918zM145.335 896.731c-48.64 0-88.21-39.497-88.21-88.137a88.283 88.283 0 01176.493 0c0 48.64-39.643 88.137-88.283 88.137zm759.735-471.04a29.55 29.55 0 00-8.265-20.48 30.793 30.793 0 00-20.48-8.265H541.257V291.182a147.31 147.31 0 00117.906-144.238 147.163 147.163 0 00-294.253 0c0 71.826 49.883 131.51 118.125 144.238-.146.365-1.974 105.691-1.974 105.691H147.675a28.453 28.453 0 00-20.48 8.265 30.135 30.135 0 00-8.923 20.48l.658 241.591C59.685 676.718.585 737.865.585 810.935c0 81.042 65.975 147.017 147.09 147.017 81.189 0 147.164-65.975 147.164-147.017 0-70.656-53.102-131.438-112.128-143.726V464.97h300.324v191.488c0 3.73.878 7.095 2.048 10.24A147.017 147.017 0 00364.91 810.935a147.237 147.237 0 00294.253 0c0-71.9-52.077-131.657-120.539-144.238a29.11 29.11 0 001.975-10.24V464.97h300.617v202.24c-64.366 14.117-112.055 73.143-112.055 143.726a147.237 147.237 0 00294.254 0c0-72.997-57.344-134.656-118.345-144.53.365-1.098 0-240.787 0-240.787z"></path>
    </svg>
        </Navbar.Toggle>
        <Navbar.Collapse id="navbarScroll">
          <Nav className="me-auto  my-lg-0" navbarScroll>
            <Link className={classes.nav_link} to="/tournaments">
              <h5>Tournaments</h5>
            </Link>
            <Link className={classes.nav_link} to="/create_tournament">
              <h5>Create Tournament</h5>
            </Link>
          </Nav>
          <ThemeSwitcher></ThemeSwitcher>
          {user ? (
            <>
              <Nav className="ml-auto" navbar>
                <Dropdown>
                  <Dropdown.Toggle
                    drop="down"
                    className={`${classes.my_toggle} ${"shadow-none"}`}
                  >
                    <SettingIcon />
                  </Dropdown.Toggle>
                  <Dropdown.Menu className={classes.my_drop}>
                    <Dropdown.Item
                      className={classes.nav_item}
                      as={Link}
                      to={`/profile/${user.username}`}
                    >
                      Profile
                    </Dropdown.Item>
                    <Dropdown.Item
                      className={classes.nav_item}
                      as={Link}
                      to="/"
                      onClick={logoutUser}
                    >
                      Log out
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </Nav>
            </>
          ) : (
            <>
              <Nav className="my-lg-0" navbarScroll>
                <Link to="/login" className={classes.nav_link}>
                  <h5>Log in</h5>
                </Link>
                <Link to="/register" className={classes.nav_link}>
                  <h5>Sign up</h5>
                </Link>
              </Nav>

            </>
          )}
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default MyNavbar;
