const API_SERVER = "http://127.0.0.1:8000/api/v1"


const tournamentApi = {
    getAllTournaments: async (limit=12, page=1, title="", game="") => {
        const response = await axios.get(`${API_SERVER}/tournaments/`, 
        {params: {
            limit: limit,
            offset: (page-1)*limit,
            title: title,
            game: game
        }})
        return  response
    },

    createModerator: async (api, data) =>  api.post(`${API_SERVER}/create_moderator/`, data),
    deleteModerator: async (api, data) =>  api.delete(`${API_SERVER}/delete_moderator/`, data),

}

export default tournamentApi
